import{_ as t,c as l,o as i,a4 as r}from"./chunks/framework.CpVK_f0P.js";const m=JSON.parse('{"title":"英语时态总结","description":"","frontmatter":{"title":"英语时态总结"},"headers":[],"relativePath":"tense/summary.md","filePath":"tense/summary.md"}'),a={name:"tense/summary.md"},o=r('<h1 id="英语时态总结" tabindex="-1">英语时态总结 <a class="header-anchor" href="#英语时态总结" aria-label="Permalink to &quot;英语时态总结&quot;">​</a></h1><p>英语中有16种基本时态，是由4种基本时间（现在、过去、将来、过去将来）和4种基本状态（一般、进行、完成、完成进行）组合而成。</p><h2 id="时态概览表" tabindex="-1">时态概览表 <a class="header-anchor" href="#时态概览表" aria-label="Permalink to &quot;时态概览表&quot;">​</a></h2><table><thead><tr><th>基本状态\\基本时间</th><th>现在</th><th>过去</th><th>将来</th><th>过去将来</th></tr></thead><tbody><tr><td>一般</td><td>一般现在时</td><td>一般过去时</td><td>一般将来时</td><td>过去将来时</td></tr><tr><td>进行</td><td>现在进行时</td><td>过去进行时</td><td>将来进行时</td><td>过去将来进行时</td></tr><tr><td>完成</td><td>现在完成时</td><td>过去完成时</td><td>将来完成时</td><td>过去将来完成时</td></tr><tr><td>完成进行</td><td>现在完成进行时</td><td>过去完成进行时</td><td>将来完成进行时</td><td>过去将来完成进行时</td></tr></tbody></table><h2 id="基本结构" tabindex="-1">基本结构 <a class="header-anchor" href="#基本结构" aria-label="Permalink to &quot;基本结构&quot;">​</a></h2><h3 id="现在时态" tabindex="-1">现在时态 <a class="header-anchor" href="#现在时态" aria-label="Permalink to &quot;现在时态&quot;">​</a></h3><ol><li><strong>一般现在时</strong>: 主语 + V(第三人称+s) / do/does + V</li><li><strong>现在进行时</strong>: 主语 + am/is/are + V-ing</li><li><strong>现在完成时</strong>: 主语 + have/has + V过去分词</li><li><strong>现在完成进行时</strong>: 主语 + have/has been + V-ing</li></ol><h3 id="过去时态" tabindex="-1">过去时态 <a class="header-anchor" href="#过去时态" aria-label="Permalink to &quot;过去时态&quot;">​</a></h3><ol><li><strong>一般过去时</strong>: 主语 + V过去式 / did + V</li><li><strong>过去进行时</strong>: 主语 + was/were + V-ing</li><li><strong>过去完成时</strong>: 主语 + had + V过去分词</li><li><strong>过去完成进行时</strong>: 主语 + had been + V-ing</li></ol><h3 id="将来时态" tabindex="-1">将来时态 <a class="header-anchor" href="#将来时态" aria-label="Permalink to &quot;将来时态&quot;">​</a></h3><ol><li><strong>一般将来时</strong>: 主语 + will + V / be going to + V</li><li><strong>将来进行时</strong>: 主语 + will be + V-ing</li><li><strong>将来完成时</strong>: 主语 + will have + V过去分词</li><li><strong>将来完成进行时</strong>: 主语 + will have been + V-ing</li></ol><h3 id="过去将来时态" tabindex="-1">过去将来时态 <a class="header-anchor" href="#过去将来时态" aria-label="Permalink to &quot;过去将来时态&quot;">​</a></h3><ol><li><strong>过去将来时</strong>: 主语 + would + V / was/were going to + V</li><li><strong>过去将来进行时</strong>: 主语 + would be + V-ing</li><li><strong>过去将来完成时</strong>: 主语 + would have + V过去分词</li><li><strong>过去将来完成进行时</strong>: 主语 + would have been + V-ing</li></ol><h2 id="主要用法特点" tabindex="-1">主要用法特点 <a class="header-anchor" href="#主要用法特点" aria-label="Permalink to &quot;主要用法特点&quot;">​</a></h2><h3 id="一般时态" tabindex="-1">一般时态 <a class="header-anchor" href="#一般时态" aria-label="Permalink to &quot;一般时态&quot;">​</a></h3><ul><li><strong>一般现在时</strong>: 表示经常性、习惯性动作或客观事实</li><li><strong>一般过去时</strong>: 表示过去发生的动作或状态</li><li><strong>一般将来时</strong>: 表示将来发生的动作或状态</li><li><strong>过去将来时</strong>: 表示过去某时刻对将来的预期</li></ul><h3 id="进行时态" tabindex="-1">进行时态 <a class="header-anchor" href="#进行时态" aria-label="Permalink to &quot;进行时态&quot;">​</a></h3><ul><li><strong>现在进行时</strong>: 表示现在正在进行的动作</li><li><strong>过去进行时</strong>: 表示过去某时刻正在进行的动作</li><li><strong>将来进行时</strong>: 表示将来某时刻正在进行的动作</li><li><strong>过去将来进行时</strong>: 表示从过去某时刻看将来正在进行的动作</li></ul><h3 id="完成时态" tabindex="-1">完成时态 <a class="header-anchor" href="#完成时态" aria-label="Permalink to &quot;完成时态&quot;">​</a></h3><ul><li><strong>现在完成时</strong>: 表示过去发生的动作对现在造成的影响</li><li><strong>过去完成时</strong>: 表示过去某时刻之前已经完成的动作</li><li><strong>将来完成时</strong>: 表示将来某时刻之前将完成的动作</li><li><strong>过去将来完成时</strong>: 表示从过去看将来某时刻之前要完成的动作</li></ul><h3 id="完成进行时态" tabindex="-1">完成进行时态 <a class="header-anchor" href="#完成进行时态" aria-label="Permalink to &quot;完成进行时态&quot;">​</a></h3><ul><li><strong>现在完成进行时</strong>: 表示从过去持续到现在的动作</li><li><strong>过去完成进行时</strong>: 表示从过去某时持续到过去另一时刻的动作</li><li><strong>将来完成进行时</strong>: 表示从现在持续到将来某时刻的动作</li><li><strong>过去将来完成进行时</strong>: 表示从过去持续到过去将来某时刻的动作</li></ul><h2 id="时态选择要点" tabindex="-1">时态选择要点 <a class="header-anchor" href="#时态选择要点" aria-label="Permalink to &quot;时态选择要点&quot;">​</a></h2><ol><li><p><strong>时间参照点</strong></p><ul><li>现在时态：以现在为参照点</li><li>过去时态：以过去某时刻为参照点</li><li>将来时态：以将来某时刻为参照点</li><li>过去将来时态：以过去某时刻为参照点，展望将来</li></ul></li><li><p><strong>动作特征</strong></p><ul><li>一般时态：强调动作本身</li><li>进行时态：强调动作的持续性</li><li>完成时态：强调动作的完成和结果</li><li>完成进行时态：强调动作的持续性和结果</li></ul></li><li><p><strong>常见时态搭配</strong></p><ul><li>时间状语从句中的将来时用现在时表示</li><li>间接引语中的时态要进行相应转换</li><li>条件句中的时态要遵循特定规则</li></ul></li></ol><h2 id="易混淆时态辨析" tabindex="-1">易混淆时态辨析 <a class="header-anchor" href="#易混淆时态辨析" aria-label="Permalink to &quot;易混淆时态辨析&quot;">​</a></h2><ol><li><p><strong>一般现在时 vs 现在进行时</strong></p><ul><li>I work here. (一般性工作)</li><li>I am working here. (当前正在工作)</li></ul></li><li><p><strong>一般过去时 vs 现在完成时</strong></p><ul><li>I visited Paris last year. (强调过去的动作)</li><li>I have visited Paris. (强调对现在的影响)</li></ul></li><li><p><strong>现在完成时 vs 现在完成进行时</strong></p><ul><li>I have written three letters. (强调完成的数量)</li><li>I have been writing letters. (强调持续的过程)</li></ul></li><li><p><strong>过去完成时 vs 一般过去时</strong></p><ul><li>When I arrived, he had left. (强调先后顺序)</li><li>When I arrived, he left. (强调相继发生)</li></ul></li></ol>',26),e=[o];function n(s,d,h,g,u,c){return i(),l("div",null,e)}const q=t(a,[["render",n]]);export{m as __pageData,q as default};
